mapper.update:
update media set
title = #{title}, description = #{description}, company = #{company}, genre = #{genre},
 movie = #{movie}, opening_year = #{openingYear}, age_limit = #{ageLimit}, media = #{media}
  where mno = #{mno}

mapper.select:
select mno, poster_img from media
where genre = #{genre} and movie=true
limit #{pageStart}, #{qty}
